import { StatusBar } from 'expo-status-bar';
import React, { useState, useEffect } from 'react';
import { StyleSheet, Text, View, SafeAreaView } from 'react-native';
import { Provider as PaperProvider } from "react-native-paper";

import HomeScreen from './components/1_HomeScreen/HomeScreen';
import SwapNumbers from './components/1_HomeScreen/SwapNumbers';
import IntoTheDiamond from './components/1_HomeScreen/IntoTheDiamond';
import KeepMeInMemory from './components/1_HomeScreen/KeepMeInMemory';

import AuthScreen from './components/2_AuthScreen/AuthScreen';
import Login from './components/2_AuthScreen/Login';
import SignUp from './components/2_AuthScreen/SignUp';

import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';


const Stack = createStackNavigator();
const Tab = createBottomTabNavigator();

const AuthContext = React.createContext();

function SignIn() {

  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const { signIn } = React.useContext(AuthContext);

  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      <Text>Login</Text>
      <TextInput
        placeholder="Username"
        value={username}
        onChangeText={setUsername}
      />
      <TextInput
        placeholder="Password"
        value={password}
        onChangeText={setPassword}
        secureTextEntry
      />
      <Button title="Login" onPress={() => login({ username, password })} />
    </View>
  );
}

export default function App({ navigation }) {

  const [state, dispatch] = React.useReducer(
    (prevState, action) => {
      switch (action.type) {
        case 'SIGN_IN':
          return {
            ...prevState,
            isSignout: false,
          };
        case 'SIGN_OUT':
          return {
            ...prevState,
            isSignout: true,
          };
      }
    },
    {
      isSignout: false,
    }
  );

  const authContext = React.useMemo(
    () => ({
      signIn: async data => {
        await fetch('http://localhost:5000/api/login',
          {
            method: 'POST',
            headers: {
              Accept: 'application/json',
              'Content-Type': 'application/json'
            },
            body: JSON.stringify({
              username: username,
              password: password
            })
          }
        )
        .then(response => {
          if (response.status == 200) {
            dispatch({ type: 'SIGN_IN'});
            return Promise.resolve();
          }
        })
        .catch(function (error) {
          console.log(error);
          return Promise.reject();
        })
      },
      signOut: () => dispatch({ type: 'SIGN_OUT' }),
      signUp: async data => {
        dispatch({ type: 'SIGN_IN'});
      },
    }),
    []
  );


  return(
    <AuthContext.Provider value={authContext}>
      <NavigationContainer>
      <Tab.Navigator>
        {state.isSignout ? (
          <>
            <Tab.Screen name="Login" component={SignIn} />
            <Tab.Screen name="SignUp" component={SignUp} />
          </>
        ) : (
          <>
            <Tab.Screen name="Swap Numbers" component={SwapNumbers} />
            <Tab.Screen name="Into the Diamond" component={IntoTheDiamond} />
            <Tab.Screen name="Keep Me in Memory" component={KeepMeInMemory} />
          </>
        )}
      </Tab.Navigator>
      </NavigationContainer>
    </AuthContext.Provider>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
